import React from 'react';
import './App.css';

import DeckGL from '@deck.gl/react';
import { TerrainLayer, TileLayer } from '@deck.gl/geo-layers';
import { BitmapLayer, ScatterplotLayer, TextLayer } from '@deck.gl/layers';
import { Table } from 'apache-arrow';
const logAndReturn = x => { console.log(x); return x; };
const santaOffTheMap = [0,89]; // todo: toggle visibility of layer instead of changing every GPU float buffer
class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      watery: false,
      initialViewState: {longitude: -122.4, latitude: 37.7, zoom: 5, pitch: 0 * 30, bearing: 0, maxZoom: 16},
    }
  }
  render() {
    const {watery} = this.state;
    const light = new TileLayer({
      data: './tiles/texture-{z}-{x}-{y}.png',
      id: 'light',
      tileSize: 256,
      maxZoom: 8,
      wrapLongitude: true,
      renderSubLayers: props => (
        new BitmapLayer((props), {
          data: null,
          image: props.data,
          bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
        })
      )
    })
    const lumaTopo = new TerrainLayer({
      elevationDecoder: {
        rScaler: 1024,
        gScaler: 0,
        bScaler: 0,
        offset: 0,
      },
      meshMaxError: 1,
      elevationData: './tiles/light-{z}-{x}-{y}.png',
      maxZoom: 2,
      wrapLongitude: true,
      texture: './tiles/texture-{z}-{x}-{y}.png',
      material: {
        ambient: 0.2,
        diffuse: 1,
        shininess: 128,
        specularColor: [255, 192, 64]
      }
    });
    const basePoints = [false, true].map(watertile => new TileLayer({
      data: './data-tiles/geo-tile-{z}-{x}-{y}-light-0.arrow',
      id: `point-tile-${watertile}`,
      visible: watertile === watery,
      maxZoom: 6,
      minZoom: 3,
      wrapLongitude: true,
      // maxCacheSize: 1,
      // refinementStrategy: "never",
      // pickable: true,
      getTileData: ({url}) => fetch(url).then(data => data.ok ? Table.from((data)) : (console.log({notok: data}),[])),
      // onHover: h => {
      //   const {index, picked, sourceLayer: {props: {data, onHover}}} = h;
      //   console.log({index, data, picked, h});
      //   return (onHover(logAndReturn({data,index,picked})))
      // },
      renderSubLayers: ({tile: {bbox: {north, south, east, west}}, data}) => [
        new ScatterplotLayer({
          id: `points-${north}-${south}-${east}-${west}-${watertile}`,
          data,
          // pickable: true,
          // maxCacheSize: 1,
          // refinementStrategy: "never",
          // onHover: ({data,index,picked}) => this.setState(logAndReturn({pointpick: !picked ? NaN : data.get(index)})),
          lineWidthUnits: 'pixels',
          radiusMinPixels: 10,
          radiusMaxPixels: 40,
          getRadius: 40,
          wrapLongitude: true,
          visible: watertile == watery, // todo: why double and not triple equals, only when dragging the map!
          getPosition: ({gt_lon, gt_lat, watery: w}) => (watertile !== w) ? santaOffTheMap : [gt_lon, gt_lat],
          getFillColor: [0,128,255],
          getLineColor: [0,0,0],
        }),
      ],
    }));
    const textAbovePoints = [false, true].map(watertile => new TileLayer({
      data: './data-tiles/geo-tile-{z}-{x}-{y}-light-0.arrow',
      id: `text-tile-${watertile}`,
      visible: watertile === watery,
      maxZoom: 6,
      minZoom: 3,
      wrapLongitude: true,
      getTileData: ({url}) => fetch(url).then(data => data.ok ? Table.from((data)) : (console.log({notok: data}),[])), // can we pull the columns out and return a {length: xyz} sort of a situation? will that make toggling faster? maybe make the watery and the dry tiles, and toggle with just the state?
      renderSubLayers: ({tile: {bbox: {north, south, east, west}}, data}) => [
        new TextLayer({
          id: `text-${north}-${south}-${east}-${west}-${watertile}`,
          data,
          backgroundColor: [204,153,51],
          getSize: 20,
          visible: watertile == watery,
          getText: ({site_page, watery: w}, {index, data}) => (index > 100 || watertile !== w) ? ' ' : (site_page.replace(/ [(][^)]+[)]$/,'')) ,
          lineHeight: 2.0,
          sizeUnits: 'pixels',
          wrapLongitude: true,
          getPosition: ({gt_lon, gt_lat, watery: w}, {index}) => (index > 100 || watertile !== w) ? santaOffTheMap : [gt_lon, gt_lat],
        }),
      ],
    }));
    // console.log({state: this.state});
    const textPick = !this.state.pointpick ? null : new TextLayer({
      id: `title-${this.state.pointpick.item_id}`,
      data: [this.state.pointpick.site_page],
      pickable: false,
      characterSet: this.state.pointpick.site_page.split(''),
      backgroundColor: [255,255,255],
      getText: (d) => `   ${d}   `,
      getSize: 20,
      lineHeight: 2.0,
      sizeUnits: 'pixels',
      wrapLongitude: true,
      getPosition: [this.state.pointpick.gt_lon, this.state.pointpick.gt_lat],
    });
    console.log(this.state);
    return (
      <div>
        <div>
          <DeckGL initialViewState={this.state.initialViewState} controller={true} layers={[light, basePoints, null && lumaTopo, textAbovePoints, textPick]} id={"maincanvas"} />
        </div>
        <div className="tweaks">
          <div id="colophon">Â© Lee Butterman 2020. Made in Oakland, California.</div>
          Tweaks:
          <div id="tweaks">
            Tweaks:<br/>
            <input id="watery" type="checkbox" checked={watery} onChange={e => this.setState({watery: e.target.checked})} />
            <label htmlFor="watery">{watery ? "Water only, I am on a boat" : "No water, I am on dry land"} </label>
          </div>          
        </div>
      </div>
    )
  }
}

export default App;
