import React from 'react';
import './App.css';

import DeckGL from '@deck.gl/react';
import { TerrainLayer, TileLayer } from '@deck.gl/geo-layers';
import { BitmapLayer, ScatterplotLayer, TextLayer } from '@deck.gl/layers';
import { Table } from 'apache-arrow';
const logAndReturn = x => { console.log(x); return x; };
class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {initialViewState: {longitude: -122.4, latitude: 37.7, zoom: 5, pitch: 0 * 30, bearing: 0, maxZoom: 16}}
  }
  render() {
    const light = new TileLayer({
      data: './tiles/texture-{z}-{x}-{y}.png',
      id: 'light',
      tileSize: 256,
      maxZoom: 8,
      wrapLongitude: true,
      renderSubLayers: props => (
        new BitmapLayer((props), {
          data: null,
          image: props.data,
          bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
        })
      )
    })
    const lumaTopo = new TerrainLayer({
      elevationDecoder: {
        rScaler: 1024,
        gScaler: 0,
        bScaler: 0,
        offset: 0,
      },
      meshMaxError: 1,
      elevationData: './tiles/light-{z}-{x}-{y}.png',
      maxZoom: 8,
      wrapLongitude: true,
      texture: './tiles/texture-{z}-{x}-{y}.png',
      material: {
        ambient: 0.2,
        diffuse: 1,
        shininess: 128,
        specularColor: [255, 192, 64]
      }
    });
    const dTPTSLP = new TileLayer({
      data: './data-tiles/geo-tile-{z}-{x}-{y}-light-0.arrow',
      id: 'geo-tile',
      maxZoom: 6,
      wrapLongitude: true,
      pickable: true,
      getTileData: ({url}) => fetch(url).then(data => Table.from(data)),
      onHover: ({index, picked, sourceLayer: {props: {data, onHover}}}) => (onHover(logAndReturn({data,index,picked}))),
      renderSubLayers: ({tile: {bbox: {north, south, east, west}}, data}) => [
        new ScatterplotLayer({
          id: `points-${north}-${south}-${east}-${west}`,
          data,
          pickable: true,
          onHover: ({data,index,picked}) => this.setState(logAndReturn({pointpick: !picked ? NaN : data.get(index)})),
          lineWidthUnits: 'pixels',
          radiusMinPixels: 10,
          radiusMaxPixels: 40,
          getRadius: 40,
          wrapLongitude: true,
          getPosition: ({gt_lon, gt_lat}) => [gt_lon, gt_lat],
          getFillColor: [0,128,255],
          getLineColor: [0,0,0],
        })
      ]
    })
    console.log({state: this.state});
    const textPick = !this.state.pointpick ? null : new TextLayer({
      id: `title-${this.state.pointpick.item_id}`,
      data: [this.state.pointpick.site_page],
      pickable: false,
      characterSet: this.state.pointpick.site_page.split(''),
      backgroundColor: [255,255,255],
      getText: (d) => `   ${d}   `,
      getSize: 20,
      lineHeight: 2.0,
      sizeUnits: 'pixels',
      wrapLongitude: true,
      getPosition: [this.state.pointpick.gt_lon, this.state.pointpick.gt_lat],
    });
    return (
      <div>
        <div>
          <DeckGL initialViewState={this.state.initialViewState} controller={true} layers={[light, dTPTSLP, null && lumaTopo, textPick]} id={"maincanvas"} />
        </div>
        {/* <div id="colophon">© Lee Butterman 2020. Made in Oakland, California.</div>
        <div>

        </div> */}
      </div>
    )
  }
}

export default App;
