class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      watery: false,
      labelspertile: 3,
      initialViewState: {
        longitude: -122.4,
        latitude: 37.7,
        zoom: 5,
        pitch: 0 * 30,
        bearing: 0,
        maxZoom: 16
      }
    };
  }

  render() {
    const {
      watery,
      labelspertile
    } = this.state;
    const light = new TileLayer({
      data: './tiles/texture-{z}-{x}-{y}.png',
      id: 'light',
      tileSize: 256,
      maxZoom: 8,
      wrapLongitude: true,
      renderSubLayers: props => new BitmapLayer(props, {
        data: null,
        image: props.data,
        bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north]
      })
    });
    const lumaTopo = new TerrainLayer({
      elevationDecoder: {
        rScaler: 1024,
        gScaler: 0,
        bScaler: 0,
        offset: 0
      },
      meshMaxError: 1,
      elevationData: './tiles/light-{z}-{x}-{y}.png',
      maxZoom: 2,
      wrapLongitude: true,
      texture: './tiles/texture-{z}-{x}-{y}.png',
      material: {
        ambient: 0.2,
        diffuse: 1,
        shininess: 128,
        specularColor: [255, 192, 64]
      }
    });
    const basePoints = [false, true].map(watertile => new TileLayer({
      data: `./data-tiles/geo-tile-{z}-{x}-{y}-light-0.arrow`,
      id: `point-tile-${watertile}`,
      visible: watertile === watery,
      maxZoom: 3,
      minZoom: 3,
      wrapLongitude: true,
      getTileData: ({
        url
      }) => fetch(url).then(data => data.ok ? Table.from(data).then(d => ({
        lng: d.getColumn('gt_lon').toArray(),
        lat: d.getColumn('gt_lat').toArray(),
        w: d.getColumn('watery').toArray(),
        length: d.getColumn('watery').toArray().length
      })) : (console.log({
        notok: data
      }), [])),
      renderSubLayers: ({
        tile: {
          bbox: {
            north,
            south,
            east,
            west
          }
        },
        data
      }) => [new ScatterplotLayer({
        id: `points-${north}-${south}-${east}-${west}-${watertile}`,
        data,
        lineWidthUnits: 'pixels',
        radiusMinPixels: 10,
        radiusMaxPixels: 40,
        getRadius: 40,
        wrapLongitude: true,
        visible: watertile == watery,
        // todo: why double and not triple equals, only when dragging the map!
        getPosition: (object, {
          index,
          data: {
            lng,
            lat,
            w
          }
        }) => watertile !== w[index] ? santaOffTheMap : [lng[index], lat[index]],
        getFillColor: [0, 128, 255],
        getLineColor: [0, 0, 0]
      })]
    }));
    const textAbovePoints = [false, true].map(watertile => new TileLayer({
      data: `./data-tiles/geo-tile-{z}-{x}-{y}-light-0.arrow`,
      id: `text-tile-${watertile}-${labelspertile}`,
      visible: watertile === watery,
      maxZoom: 6,
      minZoom: 3,
      wrapLongitude: true,
      getTileData: ({
        url
      }) => fetch(url).then(data => data.ok ? Table.from(data).then(d => ({
        lng: d.getColumn('gt_lon').toArray(),
        lat: d.getColumn('gt_lat').toArray(),
        w: d.getColumn('watery').toArray(),
        site_page: d.getColumn('site_page').toArray(),
        length: Math.floor(Math.pow(10, labelspertile / 2)) // d.getColumn('watery').toArray().length,

      })) : (console.log({
        notok: data
      }), [])),
      renderSubLayers: ({
        tile: {
          bbox: {
            north,
            south,
            east,
            west
          }
        },
        data
      }) => [new TextLayer({
        id: `text-${north}-${south}-${east}-${west}-${watertile}`,
        data,
        characterSet,
        backgroundColor: [204, 153, 51],
        getSize: 20,
        visible: watertile == watery,
        getText: (object, {
          index,
          data: {
            site_page,
            w
          }
        }) => index > 100 || watertile !== w[index] ? ' ' : site_page[index].replace(/ [(][^)]+[)]$/, ''),
        lineHeight: 2.0,
        sizeUnits: 'pixels',
        wrapLongitude: true,
        getPosition: (object, {
          index,
          data: {
            lng,
            lat,
            w
          }
        }) => index > 100 || watertile !== w[index] ? santaOffTheMap : [lng[index], lat[index]]
      })]
    })); // console.log({state: this.state});

    const textPick = !this.state.pointpick ? null : new TextLayer({
