    return (
      <div>
        <div>
          <DeckGL initialViewState={this.state.initialViewState} controller={true} layers={[light, basePoints, null && lumaTopo, textAbovePoints, textPick]} id={"maincanvas"} />
        </div>
        <div className="tweaks">
          <div id="colophon">© Lee Butterman 2020. Made in Oakland, California.</div>
          Tweaks:
          <div id="tweaks">
            Tweaks:<br/>
            <input id="watery" type="checkbox" checked={watery} onChange={e => this.setState({watery: e.target.checked})} />
            <label htmlFor="watery">{watery ? "Water only, I am on a boat" : "No water, I am on dry land"} </label><br/>
            <input id="labelspertile" type="range" min="-1" max="6" step="1" value={labelspertile} onChange={e => this.setState({labelspertile: e.target.value * 1})} />
            Display {Math.floor(Math.pow(10, labelspertile/2))}.0 labels per tile <br/>

          </div>          
        </div>
      </div>
    )
  }
}

export default App;
