import React from 'react';
import './App.css';

import DeckGL from '@deck.gl/react';
import { TerrainLayer, TileLayer } from '@deck.gl/geo-layers';
import { BitmapLayer, ScatterplotLayer, TextLayer } from '@deck.gl/layers';
import { Table } from 'apache-arrow';

const logAndReturn = (x) => { console.log(x); return x };

class SingleBatchArrowTileLayer extends TileLayer {
  async getTileData(tile) {
    const {url} = this.props;
    // console.log({tiledata: data});
    if(!url) { return null }
    const table = await Table.from(fetch(url, {layer: this}));
    // console.log({table});
    return table;
  }
}

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {initialViewState: {longitude: -122.4, latitude: 37.7, zoom: 5, pitch: 30, bearing: 0, maxZoom: 8}}
  }
  render() {
    const light = new TileLayer({
      data: './tiles/light-{z}-{x}-{y}.png',
      tileSize: 1024,
      maxZoom: 6,
      wrapLongitude: true,
      renderSubLayers: props => (
        new BitmapLayer(logAndReturn(props), {
          data: null,
          image: props.data,
          bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
        })
      )
    })
    const lumaTopo = new TerrainLayer({
      elevationDecoder: {
        rScaler: 500,
        gScaler: 0,
        bScaler: 0,
        offset: 0,
      },
      meshMaxError: 1,
      elevationData: './tiles/light-{z}-{x}-{y}.png',
      maxZoom: 8,
      wrapLongitude: true,
      texture: './tiles/texture-{z}-{x}-{y}.png',
      material: {
        ambient: 0.2,
        diffuse: 1,
        shininess: 128,
        specularColor: [255, 192, 64]
      }
    });
    const dataTilesPointText = new SingleBatchArrowTileLayer({
      data: './data-tiles/geo-tile-{z}-{x}-{y}-light-0.arrow',
      maxZoom: 6,
      wrapLongitude: true,
      pickable: true,
      renderSubLayers: ({tile: {bbox: {north, south, east, west}}, data}) => {
        // data.then(thendata => console.log({thendata}));
        const points = new ScatterplotLayer({
          id: `points-${north}-${south}-${east}-${west}`,
          data,
          pickable: true,
          autoHighlight: true,
          onHover: h => alert(h),
          onClick: h => console.log({hiIGotClicked: h}),
          //  ({object, picked}) => this.setState({
          //   pointpick: logAndReturn(picked) ? data.get(index) : null}),
          radiusMinPixels: 5,
          radiusMaxPixels: 40,
          lineWidthUnits: 'pixels',
          getRadius: 40,
          wrapLongitude: true,
          getPosition: ({gt_lon, gt_lat}, {index, target}) => [gt_lon, gt_lat],
          getFillColor: [100,100,150],
          getLineColor: [0,0,0],  
        });
        return [points];
      }
    });
    console.log({state: this.state});
    const textPick = !this.state.pointpick ? null : new TextLayer({
      id: `title-${this.state.pointpick.item_id}`,
      data: [this.state.pointpick.site_page],
      pickable: false,
      characterSet: this.state.pointpick.site_page.split(''),
      backgroundColor: [255,255,255],
      getText: (d) => `   ${d}   `,
      getSize: 20,
      sizeUnits: 'pixels',
      wrapLongitude: true,
      getPosition: [this.state.pointpick.gt_lon, this.state.pointpick.gt_lat],
    });
    return (
      <div>
        <div>
          <DeckGL initialViewState={this.state.initialViewState} controller={true} layers={[lumaTopo && null, dataTilesPointText, textPick]} id={"maincanvas"} />
        </div>
        {/* <div id="colophon">Â© Lee Butterman 2020. Made in Oakland, California.</div>
        <div>

        </div> */}
      </div>
    )
  }
}

export default App;
