import React from 'react';
import './App.css';
import DeckGL from '@deck.gl/react';
import { TerrainLayer, TileLayer, } from '@deck.gl/geo-layers';
import { BitmapLayer,  } from '@deck.gl/layers';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      viz: "bumpy-light",
      initialViewState: {longitude: -122.4, latitude: 37.7, zoom: 5, pitch: 30, bearing: 0, maxZoom: 8},
    }
  }
  render() {
    const {viz} = this.state;
    console.log({viz});
    const smog = new TileLayer({
      data: './tiles/greys-{z}-{x}-{y}.png',
      id: 'smog',
      wrapLongitude: true,
      renderSubLayers: props => (
        new BitmapLayer((props), {
          data: null,
          image: props.data,
          bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
        })
      )
    })
    const watercolor = new TileLayer({
      data: './stamen-tiles/watercolor/watercolor-{z}-{x}-{y}.jpg',
      id: 'watercolor',
      wrapLongitude: true,
      renderSubLayers: props => new BitmapLayer((props), {
        data: null,
        image: props.data,
        bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
      })
    });
    const tonerlabel = new TileLayer({
      data: './stamen-tiles/toner-labels/toner-labels-{z}-{x}-{y}.png',
      id: 'tonerlabel',
      wrapLongitude: true,
      renderSubLayers: props => new BitmapLayer((props), {
        data: null,
        image: props.data,
        bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
      })
    });
    const terrain = new TileLayer({
      data: './stamen-tiles/terrain/terrain-{z}-{x}-{y}.png',
      id: 'greenhills',
      wrapLongitude: true,
      renderSubLayers: props => new BitmapLayer((props), {
        data: null,
        image: props.data,
        bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
      })
    })
    const bumpyLight = new TerrainLayer({
      id: 'bumpyLight',
      elevationDecoder: {
        rScaler: 1024,
        gScaler: 0,
        bScaler: 0,
        offset: 0,
      },
      meshMaxError: 1,
      elevationData: './tiles/light-{z}-{x}-{y}.png',
      wrapLongitude: true,
      texture: './tiles/water-{z}-{x}-{y}.png',
      material: {
        ambient: 0.2,
        diffuse: 1,
        shininess: 128,
        specularColor: [255, 192, 64]
      }
    });
    const bumpyTerrain = new TerrainLayer({
      id: 'bumpyTerrain',
      elevationDecoder: {
        rScaler: 1024,
        gScaler: 0,
        bScaler: 0,
        offset: 0,
      },
      meshMaxError: 1,
      elevationData: './tiles/light-{z}-{x}-{y}.png',
      wrapLongitude: true,
      texture: './stamen-tiles/terrain/terrain-{z}-{x}-{y}.png',
      material: {
        ambient: 0.2,
        diffuse: 1,
        shininess: 128,
        specularColor: [255, 192, 64],
      },
    });

    const layers = viz === "bumpy-light" ? [bumpyLight] : (viz === "bumpy-terrain" ? [bumpyTerrain] : (viz === "hazy-terrain" ? [terrain, smog] : [watercolor, tonerlabel, smog]) );
    return (
      <div>
        <div>
          <DeckGL initialViewState={this.state.initialViewState} controller={true} layers={layers} id={"maincanvas"} />
        </div>
        <div className="tweaks">
          <div id="colophon">© Lee Butterman 2020. Made in Oakland, California.</div>
          <div id="tweaks">
            Tweaks:<br/>
            <form>
              <div>
                <label>
                  <input type="radio" name="viz" value="bumpy-light" checked={viz === "bumpy-light"} onChange={e => this.setState({viz: e.target.value})} />
                  Light-pollution as topography, light-pollution + water as texture
                </label>
              </div>
              <div>
                <label>
                  <input type="radio" name="viz" value="bumpy-terrain" checked={viz === "bumpy-terrain"} onChange={e => this.setState({viz: e.target.value})} />
                  Light-pollution as topography, terrain map as texture
                </label>
              </div>
              <div>
                <label>
                  <input type="radio" name="viz" value="hazy-terrain" checked={viz === "hazy-terrain"} onChange={e => this.setState({viz: e.target.value})} />
                  Light-pollution as haze on terrain
                </label>
              </div>
              <div>
                <label>
                  <input type="radio" name="viz" value="hazy-watercolor" checked={viz === "hazy-watercolor"} onChange={e => this.setState({viz: e.target.value})} />
                  Light-pollution as haze on watercolor
                </label>
              </div>
            </form>
          </div>          
        </div>
      </div>
    )
  }
}

export default App;
