import React from 'react';
import './App.css';
import DeckGL from '@deck.gl/react';
import { TerrainLayer, TileLayer } from '@deck.gl/geo-layers';
import { BitmapLayer, ScatterplotLayer, TextLayer } from '@deck.gl/layers';
import { Table } from 'apache-arrow';

const logAndReturn = (x) => { console.log(x); return x };

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {initialViewState: {longitude: -122.4, latitude: 37.7, zoom: 5, pitch: 30, bearing: 0, maxZoom: 8}}
  }
  render() {
    const light = new TileLayer({
      data: './tiles/light-{z}-{x}-{y}.png',
      tileSize: 1024,
      maxZoom: 6,
      wrapLongitude: true,
      renderSubLayers: props => (
        new BitmapLayer(logAndReturn(props), {
          data: null,
          image: props.data,
          bounds: [props.tile.bbox.west, props.tile.bbox.south, props.tile.bbox.east, props.tile.bbox.north],
        })
      )
    })
    const lumaTopo = new TerrainLayer({
      elevationDecoder: {
        rScaler: 500,
        gScaler: 0,
        bScaler: 0,
        offset: 0,
      },
      meshMaxError: 1,
      elevationData: './tiles/light-{z}-{x}-{y}.png',
      maxZoom: 8,
      wrapLongitude: true,
      texture: './tiles/texture-{z}-{x}-{y}.png',
      material: {
        ambient: 0.2,
        diffuse: 1,
        shininess: 128,
        specularColor: [255, 192, 64]
      }
    });
    const dataTilesPointText = new TileLayer({
      data: './data-tiles/geo-tile-{z}-{x}-{y}-light-0.arrow',
      maxZoom: 7,
      wrapLongitude: true,
      renderSubLayers: ({tile: {bbox: {north, south, east, west}}, data}) => {
        console.log(data);
        const table = Table.from(data);
        const lat = table.getColumn('gt_lat').toArray();
        const lon = table.getColumn('gt_lon').toArray();
        const points = new ScatterplotLayer({
          id: `points-{north}-{south}-{east}-{west}`,
          data: {length: lat.length},
          pickable: true,
          onHover: ({index, picked}) => this.setState({pointpick: picked ? table.get(index) : null}),
          getRadius: 10,
          wrapLongitude: true,
          getPosition: (object, {index, data, target}) => [lon[index], lat[index]],
          getFillColor: [100,100,150],
          getLineColor: [0,0,0],  
        });
      }
    });
    const textPick = !this.state.pointpick ? null : new TextLayer({
      id: `title-${this.state.pointpick.item_id}`,
      data: [this.state.pointpick.site_page],
      pickable: false,
      characterSet: this.state.pointpick.site_page.split(''),
      backgroundColor: [255,255,255],
      getText: (d) => `   ${d}   `,
      getSize: 20,
      sizeUnits: 'pixels',
      wrapLongitude: true,
      getPosition: [this.state.pointpick.gt_lon, this.state.pointpick.gt_lat],
    });
    return (
      <div>
        <div>
          <DeckGL initialViewState={this.state.initialViewState} controller={true} layers={[lumaTopo, dataTilesPointText, textPick]} id={"maincanvas"} />
        </div>
        <div id="colophon">Â© Lee Butterman 2020. Made in Oakland, California.</div>
        <div>

        </div>
      </div>
    )
  }
}

export default App;
